ALTER TABLE `bms`.`community`
  CHANGE `community_no` `community_no` VARCHAR(50) CHARSET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '物业标示编号',
  CHANGE `community_name` `community_name` VARCHAR(100) CHARSET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '社区名称',
