  ADD COLUMN `community_address_direction_second_tips` VARCHAR(30) NULL COMMENT '地址方向2提示：东南' AFTER `community_address_direction_first_value`,
  ADD COLUMN `community_address_direction_second_value` VARCHAR(300) NULL COMMENT '地址方向2值：华发广场' AFTER `community_address_direction_second_tips`,
  ADD COLUMN `community_address_direction_third_tips` VARCHAR(30) NULL COMMENT '地址方向3提示：西北' AFTER `community_address_direction_second_value`,
  ADD COLUMN `community_address_direction_third_value` VARCHAR(300) NULL COMMENT '地址方向3值：历史大厦' AFTER `community_address_direction_third_tips`,
  ADD COLUMN `community_address_direction_fourth_tips` VARCHAR(30) NULL COMMENT '地址方向4提示：西南' AFTER `community_address_direction_third_value`,
  ADD COLUMN `community_address_direction_fourth_value` VARCHAR(300) NULL COMMENT '地址方向4值：历史大厦' AFTER `community_address_direction_fourth_tips`,
  ADD COLUMN `community_cover_area` DECIMAL(12,2) NULL COMMENT '覆盖面积' AFTER `community_area`,
  ADD COLUMN `community_no_cover_area` DECIMAL(12,2) NULL COMMENT '无覆盖面积' AFTER `community_area`,
  ADD COLUMN `community_vertical_area` DECIMAL(12,2) NULL COMMENT '垂直占用空间面积' AFTER `community_no_cover_area`,
  ADD COLUMN `community_shops_area` DECIMAL(12,2) NULL COMMENT '商铺面积' AFTER `community_vertical_area`,
  ADD COLUMN `community_house_area` DECIMAL(12,2) NULL COMMENT '住宅面积' AFTER `community_shops_area`,
